-- --------------------------------------- PARADIGM INSERTS ----------------------------------------
INSERT INTO paradigm (pName, description) VALUES ('Object-Oriented', 'Everything in this paradigm is modelled as an object.');
INSERT INTO paradigm (pName, description) VALUES ('Functional', 'Each instruction is modelled as a function, with some input and some output.');
INSERT INTO paradigm (pName, description) VALUES ('Procedural', 'Possibly the simplest paradigm. A program is simply a list of instructions, read line by line by the compiler.');
INSERT INTO paradigm (pName, description) VALUES ('Logical', 'A program in the logical paradigm is simply a list of rules and facts.  These can be queried when the program is running, returning a value of true or false.');
INSERT INTO paradigm (pName, description) VALUES ('Declarative', 'The program code does not specify how to solve a problem line by line.  Instead it provides a set of rules which can be used to solve the problem at runtime.');
INSERT INTO paradigm (pName, description) VALUES ('Event-Driven', 'The way in which the program is run depends on which events occur and in what order.  An event may be a button click or the pressing of a particular key on the keyboard, for example.');
INSERT INTO paradigm (pName, description) VALUES ('Parallel', 'Allows for the running of different threads concurrently.');
INSERT INTO paradigm (pName, description) VALUES ('Markup', 'Not really a paradigm.  A way of programming where evrything is structured in blocks.');
INSERT INTO paradigm (pName, description) VALUES ('Scripting', 'Simple languages that are fairly written in small files.  Scripts are usually used for quick, simple tasks, for example on web pages.');
INSERT INTO paradigm (pName, description) VALUES ('Block', 'A programming style that allows for programs to be written using visual (usually coloured) blocks.');

