INSERT INTO language (lName, description, year, mainParadigm, highOrLow) VALUES ('ARM Assembly', 'ARM (Advanced RISC machine) is a specific architecture for computer processors. ARM Assembly is a low level language that can only be used on this architecture.','1985','Procedural', 'Low-Level');
INSERT INTO language (lName, description, year, mainParadigm, highOrLow) VALUES ('C', 'A general purpose, systems level programming language. C has syntax like a high-level language, but also some capabilities you would expect to find in a low-level language','1972','Procedural', 'Low-Level');
INSERT INTO language (lName, description, year, mainParadigm, highOrLow) VALUES ('C#', 'Microsofts alternative to the C programming language. C# (pronounced "C sharp").  It is a general purpose language which makes use of many paradigms, however it is mainly object-oriented.','2000','Object-Oriented', 'High-Level');
INSERT INTO language (lName, description, year, mainParadigm, highOrLow) VALUES ('CakePHP', 'A PHP framework used to build dynamic websites quickly using the Model-View-Controller approach.','2000','Scripting', 'High-Level');
INSERT INTO language (lName, description, year, mainParadigm, highOrLow) VALUES ('C++', 'C++ (C plus plus) has all the features of the C programming language but with the ability for object-oriented programming too.','1985','Object-Oriented', 'High-Level');
INSERT INTO language (lName, description, year, mainParadigm, highOrLow) VALUES ('CSS', 'CSS or Cascading Style Sheets is a style sheet language used to control the structure of a language written in a markup language, for example HTML.  If you want to make websites that look nice, you should know CSS!','1996','Procedural', 'High-Level');
