INSERT INTO language (lName, description, year, mainParadigm, highOrLow) VALUES ('Erlang', 'A general purpose, concurrent, functional programming language.','1986','Functional', 'High-Level');
INSERT INTO language (lName, description, year, mainParadigm, highOrLow) VALUES ('Event-ML', 'An event-driven version of the classic functional language, ML','0001','Event-Driven', 'High-Level');
INSERT INTO language (lName, description, year, mainParadigm, highOrLow) VALUES ('F#', 'F# is a strongly typed, open source, cross-platform programming language.  It empowers users to tackle complex computing problems with simple, maintainable and robust code','2005','Functional', 'High-Level');
INSERT INTO language (lName, description, year, mainParadigm, highOrLow) VALUES ('Go', 'Go, also known as golang, was created by Google in 2009.  It is a compiled, statically typed language in the tradition of Algol and C, with features such as garbage collection and limited structural typing.','2009','Procedural', 'High-Level');
INSERT INTO language (lName, description, year, mainParadigm, highOrLow) VALUES ('Haskell', 'A purely functional programming language, with non-strict semantics and strong static typing.','1990','Functional', 'High-Level');
INSERT INTO language (lName, description, year, mainParadigm, highOrLow) VALUES ('HTML', 'This is the standard markup language for creating web pages. HTML stands for Hyper Text Markup Language.','1993','Markup', 'High-Level');
INSERT INTO language (lName, description, year, mainParadigm, highOrLow) VALUES ('Java', 'A general-purpose, object-oriented, class-based language used in many areas including server-side scripting and mobile app development.','1996','Object-Oriented', 'High-Level');
INSERT INTO language (lName, description, year, mainParadigm, highOrLow) VALUES ('JavaScript', 'Not to be confused with Java! JavaScript is a high-level, dynamic, weakly typed language, mostly used for scripting on world wide web pages.','1995','Scripting', 'High-Level');
INSERT INTO language (lName, description, year, mainParadigm, highOrLow) VALUES ('LISP', 'Lisp is the second-oldest high-level language still used today.  There have been many versions of Lisp, but it is best known for its distinctive, fully parenthesized notation.','1958','Functional', 'High-Level');
INSERT INTO language (lName, description, year, mainParadigm, highOrLow) VALUES ('MatLab', 'A multi-paradigm numerical computing environment.','1984','Logical', 'High-Level');
INSERT INTO language (lName, description, year, mainParadigm, highOrLow) VALUES ('MIPS Assembly', 'MIPS is a low-level programmimng language that can be used to manipulate computer processors with the MIPS architecture.','1985','Procedural', 'Low-Level');
