INSERT INTO language (lName, description, year, mainParadigm, highOrLow) VALUES ('OCAML', 'Objective CAML. A member of the ML language family.','1996','Functional', 'High-Level');
INSERT INTO language (lName, description, year, mainParadigm, highOrLow) VALUES ('Pascal', 'A small, efficient language, intended to encourage good programming practices.','1970','Procedural', 'High-Level');
INSERT INTO language (lName, description, year, mainParadigm, highOrLow) VALUES ('PDDL', 'A planning language.  In PDDL, a list of rules and possible actions are defined. When the program is run, it is given some goals to solve and must work out how to solve them itself.','1998','Declarative', 'High-Level');
