INSERT INTO language (lName, description, year, mainParadigm, highOrLow) VALUES ('Prolog', 'A language which uses predicate logic to model facts and rules that can be queried upon running the program.','1972','Logical', 'High-Level');
INSERT INTO language (lName, description, year, mainParadigm, highOrLow) VALUES ('Python', 'A language designed to be simple to write in.  Python can be used in almost every area of programming.','1991','Procedural', 'High-Level');
INSERT INTO language (lName, description, year, mainParadigm, highOrLow) VALUES ('R', 'A programming language and software environment used for statistical computing and graphics.','1993','Object-Oriented', 'High-Level');
INSERT INTO language (lName, description, year, mainParadigm, highOrLow) VALUES ('Scratch', 'This allows you to write programs using coloured blocks. In Scratch you can quickly create lots of simple programs where you control a character on the screen called a Sprite.','2002','Block', 'High-Level');
