INSERT INTO language (lName, description, year, mainParadigm, highOrLow) VALUES ('Swift', 'A relatively new programming language developed by Apple.  It is mainly used for developing macOS desktop applications and iOS mobile apps.','2014','Object-Oriented', 'High-Level');
